    foreach (var imagen in imagenes)
    {
        try
        {
            var arrbytes = new byte[imagen.Size];
            await imagen.OpenReadStream(Constants.maxSizeFile).ReadAsync(arrbytes);
            imagenBase64 = Convert.ToBase64String(arrbytes);
